export const loginaction =(user) =>(dispatch)=>{
    try {
        dispatch({type:ADD_USER_LODINGS})
        axios.post("https://reqres.in/api/login",user).then((data) =>{
            
            dispatch({type:ADD_USER_SUCCSESS,payload:data.data.token})
            console.log(data)
        }).catch((err) =>{
            dispatch({type:ADD_USER_LODINGS})

        })
    } catch (error) {
        
    }
}

